#include <stdio.h>long faktorial(int x) {	if (x <= 1) {		return 1;	}	else {		return x * faktorial(x - 1);	}}// kazdy den 2x vetsi cislo nez to predtimlong bunky(int x) {	if (x <= 1) {		return 1;	}	else {		return 2 * bunky(x - 1);	}}long fibo(int x) {		if (x <= 0) {		return 0;	}	if (x == 1) {		return 1;		}	else {		return fibo(x - 1) + fibo(x - 2);	}}int main() { 	printf("%d\n", bunky(1));	printf("%d\n", bunky(2));	printf("%d\n", bunky(3));	printf("%d\n", bunky(4));	printf("\n");	printf("%d\n", fibo(1));	printf("%d\n", fibo(2));	printf("%d\n", fibo(3));	printf("%d\n", fibo(4));	printf("%d\n", fibo(5));}